#设置客户端连接服务器端的编码为UTF8
set names utf8;
#是否存在数据库
drop database if exists dvy;
#创建数据库
create database dvy charset=utf8;
#使用数据库
use dvy;
#创建数据表
create table wd_movie(
`mid` int primary key auto_increment,  #编号
`title` varchar(32),	  #电影
`title_en` varchar(32),	  #英文标题
`long` varchar(32),	  #时长
`type` varchar(32),	  #影片类型
`ctime` date,	          #上映时间
`place` varchar(16),	  #上映地点
`detail` varchar(10000),  #简介
`director` varchar(32),	  #导演姓名
`dpic` varchar(128),	  #导演头像
`isPlay` boolean,	  #是否上映1-正在上映0-即将上映
`num` int,                #想看的数量
`title_id` int            #外键
);

