#用户模块
create table users(
`id` int primary key auto_increment,  #编号
`rser` varchar(32),                  #用户名
`tped` varchar(18),                  #密码
`emaile` varchar(32),                #邮箱
`nema` varchar(32),                   #姓名
`sex` int,                            #性别
`Birthday` date,                      #生日
`uphoer` varchar(11)                 #手机号码
);





