create table wd_actor(
`aid` int primary key auto_increment,   #编号	 
`pname` varchar(16),	                #姓名
`Introduction` varchar(10000),          #简介
`apic` varchar(128),	                #演员头像
`movie_id` int,	                        #所属影片的id
foreign key(movie_id) references wd_movie(mid)
);


